import CryptoJS from 'crypto-js';

const data = 'my1234567';
const iv = 'yourivare1234567';
const key = '356d9abc7532ceb0945b615a622c3370';

const fkey = CryptoJS.enc.Utf8.parse(key);
const fiv = CryptoJS.enc.Utf8.parse(iv);

const enc = CryptoJS.AES.encrypt(data, fkey, {
  iv: fiv,
  mode: CryptoJS.mode.CBC,
  padding: CryptoJS.pad.Pkcs7,
});

export const crypto = (message, key) => {
  try {
    return CryptoJS.AES.encrypt(message, key).toString();
  } catch (err) {
    console.log(err);
  }
};

export const decrypted = (cryptoMessage, key) => {
  try {
    const decrypted = CryptoJS.AES.decrypt(cryptoMessage, key).toString(CryptoJS.enc.Utf8);
    if (!decrypted || !cryptoMessage) {
      return 'Сообщение зашифровано, пожалуйста укажите секретный ключ!';
    }
    return decrypted;
  } catch (err) {
    console.log(err);
  }
};
