import CryptoJS from 'crypto-js';

export const crypto = (message, key) => {
  const crypto = CryptoJS.AES.encrypt(JSON.stringify({ message }), key).toString();
  console.log(crypto);
};

export const decrypted = (cryptoMessage, key) => {
  console.log(cryptoMessage);
  const decrypted = CryptoJS.AES.decrypt(cryptoMessage, key).toString(CryptoJS.enc.Utf8);
  if (!decrypted) {
    console.log('пустое', cryptoMessage);
    return cryptoMessage;
  }
  console.log('pfibah', decrypted);
  return decrypted;
};
