import CryptoJS from 'crypto-js';

const cipherData = fs.readFileSync(`${__dirname}/key.json`);
const { key, algorithm } = JSON.parse(cipherData);

export const encryptMessage = (message) => {
  const iv = CryptoJS.randomBytes(8).toString('hex');
  const cipher = CryptoJS.createCipheriv(algorithm, key, iv);

  let encrypted = cipher.update(message, 'utf-8', 'hex');
  encrypted += cipher.final('hex');
  return `${encrypted}:${iv}`;
};

const decryptMessage = (message) => {
  const [encryptedMessage, iv] = message.split(':');
  const decipher = CryptoJS.createDecipheriv(algorithm, key, iv);

  let decrypted = decipher.update(encryptedMessage, 'hex', 'utf-8');
  decrypted += decipher.final('utf-8');

  return decrypted;
};

console.log(decryptMessage(encryptMessage('Hi! How are you?')));
